<script setup lang="ts">
import { watch } from 'vue'

import { gsap } from 'gsap'
import { MorphSVGPlugin } from 'gsap/MorphSVGPlugin'

gsap.registerPlugin(MorphSVGPlugin)

const props = defineProps({
  activeStep: {
    type: Number,
    default: 0,
  },
})

const ids = {
  0: 'nine',
  1: 'b',
  2: 'G',
  3: 'arabic',
  4: 'loop',
}

watch(
  () => props.activeStep,
  (newValue) => {
    gsap.to('#six', {
      morphSVG: `#${ids[newValue]}`,
      duration: 1,
      ease: 'expo.inOut',
      yoyo: true,
      repeat: 1,
    })
  },
)
</script>

